#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    int P[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &P[i]);
    }
    int visited[N + 1];
    for (int i = 0; i <= N; i++) {
        visited[i] = 0;
    }
    for (int i = 1; i <= N; i++) {
        int min_index = -1;
        int min_val = 1e9;
        for (int j = 0; j < N; j++) {
            if (P[j] == i && visited[P[j]] == 0) {
                if (j < min_val) {
                    min_val = j;
                    min_index = j;
                }
            }
        }
        if (min_index != -1) {
            visited[P[min_index]] = 1;
            for (int k = 0; k < N; k++) {
                if (P[k] < P[min_index] && visited[P[k]] == 0) {
                    if (abs(P[k] - P[min_index]) == 1 && min_index - k >= K) {
                        int temp = P[k];
                        P[k] = P[min_index];
                        P[min_index] = temp;
                    }
                }
            }
        }
    }
    for (int i = 0; i < N; i++) {
        printf("%d\n", P[i]);
    }
    return 0;
}
