#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n, k;
  cin >> n >> k;

  vector<int> p(n);
  for (int i = 0; i < n; ++i) {
    cin >> p[i];
  }

  vector<int> q = p;
  vector<pair<int, int>> swaps;

  bool changed = true;
  while (changed) {
    changed = false;
    for (int i = 0; i < n; ++i) {
      for (int j = i + k; j < n; ++j) {
        if (abs(q[i] - q[j]) == 1) {
          swap(q[i], q[j]);
          changed = true;
          break;
        }
      }
      if (changed) break;
    }
  }

  for (int i = 0; i < n; ++i) {
    cout << q[i] << endl;
  }

  return 0;
}
