import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        int[] p = new int[n];
        for (int i = 0; i < n; i++) {
            p[i] = sc.nextInt();
        }
        sc.close();

        PriorityQueue<int[]> pq = new PriorityQueue<>(Comparator.comparingInt(a -> a[0]));
        pq.offer(p.clone());

        Set<String> visited = new HashSet<>();
        visited.add(Arrays.toString(p));

        while (!pq.isEmpty()) {
            int[] current = pq.poll();
            boolean swapped = false;
            for (int i = 0; i < n; i++) {
                for (int j = i + k; j < n; j++) {
                    if (Math.abs(current[i] - current[j]) == 1) {
                        int[] next = current.clone();
                        int temp = next[i];
                        next[i] = next[j];
                        next[j] = temp;
                        String nextStr = Arrays.toString(next);
                        if (!visited.contains(nextStr)) {
                            pq.offer(next);
                            visited.add(nextStr);
                            swapped = true;
                        }
                    }
                }
            }
            if (!swapped) {
                for (int i = 0; i < n; i++) {
                    System.out.println(current[i]);
                }
                return;
            }
        }
    }
}
