#include <stdio.h>

int main() {
    int A, B, C, D, E, F;
    scanf("%d %d %d %d %d %d", &A, &B, &C, &D, &E, &F);
    int max_sugar = 0;
    int max_water = 0;
    for (int i = 0; i <= 3000 / (100 * A); ++i) {
        for (int j = 0; j <= 3000 / (100 * B); ++j) {
            int water = 100 * A * i + 100 * B * j;
            int sugar_capacity = water * E / 100;
            for (int k = 0; k <= 3000 / C; ++k) {
                for (int l = 0; l <= 3000 / D; ++l) {
                    int sugar = C * k + D * l;
                    if (sugar <= sugar_capacity && water + sugar <= F) {
                        if (sugar > max_sugar) {
                            max_sugar = sugar;
                            max_water = water;
                        }
                    }
                }
            }
        }
    }
    printf("%d %d\n", max_water + max_sugar, max_sugar);
    return 0;
}
