def solve():
    n, k = map(int, input().split())
    p = list(map(int, input().split()))

    q = list(p)

    visited = {tuple(q)}
    queue = [q]

    while queue:
        curr = queue.pop(0)

        for i in range(n):
            for j in range(i + k, n):
                if abs(curr[i] - curr[j]) == 1:
                    next_perm = curr[:]
                    next_perm[i], next_perm[j] = next_perm[j], next_perm[i]

                    if tuple(next_perm) not in visited:
                        visited.add(tuple(next_perm))
                        queue.append(next_perm)

    min_perm = min(visited, key=lambda x: list(x))
    for x in min_perm:
        print(x)


