#include <iostream>

using namespace std;

int main() {
    long long a, b, c, d, e, f;
    cin >> a >> b >> c >> d >> e >> f;

    long long best_water = 0, best_sugar = 0;
    for (long long i = 0; ; ++i) {
        for (long long j = 0; ; ++j) {
            long long water = 100 * (a * i + b * j);
            for (long long k = 0; ; ++k) {
                for (long long l = 0; ; ++l) {
                    long long sugar = c * k + d * l;
                    if (sugar <= water * e / 100.0 && water + sugar <= f) {
                        if (water + sugar > best_water + best_sugar) {
                            best_water = water;
                            best_sugar = sugar;
                        }
                    }
                    if (c * k + d * l > f) break;
                    if (l>1000) break;
                }
                if (c * k > f) break;
                if (k > 1000) break;

            }
            if (100 * (a * i + b * j) > f) break;
            if (j > 1000) break;
        }
        if (100 * a * i > f) break;
        if (i > 1000) break;
    }

    cout << best_water + best_sugar << " " << best_sugar << endl;

    return 0;
}
