A, B, C, D, E, F = map(int, input().split())

best_sugar = 0
best_water = 0

for water_count_A in range(F // (100 * A) + 1):
    for water_count_B in range(F // (100 * B) + 1):
        water = 100 * A * water_count_A + 100 * B * water_count_B
        max_sugar = water * E // 100
        for sugar_count_C in range(F // C + 1):
            for sugar_count_D in range(F // D + 1):
                sugar = C * sugar_count_C + D * sugar_count_D
                if sugar <= max_sugar and water + sugar <= F:
                    if sugar > best_sugar:
                        best_sugar = sugar
                        best_water = water

print(best_water + best_sugar, best_sugar)
