#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    long long A[N][N];
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            scanf("%lld", &A[i][j]);
        }
    }
    long long total_length = 0;
    long long edges[N][N];
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            edges[i][j] = 0;
        }
    }
    for (int i = 0; i < N; i++) {
        for (int j = i + 1; j < N; j++) {
            long long min_dist = A[i][j];
            for (int k = 0; k < N; k++) {
                if (k != i && k != j) {
                    if (A[i][k] + A[k][j] < min_dist) {
                        min_dist = -1;
                        break;
                    }
                }
            }
            if (min_dist == -1) {
                printf("-1\n");
                return 0;
            }
            edges[i][j] = min_dist;
            edges[j][i] = min_dist;
            total_length += min_dist;
        }
    }
    printf("%lld\n", total_length);
    return 0;
}
