#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    if (n == 1) {
        int x;
        scanf("%d", &x);
        if (x >= 0)
            printf("POSSIBLE\n");
        else
            printf("IMPOSSIBLE\n");
        return 0;
    }
    int *p = (int *)malloc((n - 1) * sizeof(int));
    int *x = (int *)malloc(n * sizeof(int));
    for (int i = 0; i < n - 1; i++) {
        scanf("%d", &p[i]);
        p[i]--;
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &x[i]);
    }
    int **adj = (int **)malloc(n * sizeof(int *));
    for (int i = 0; i < n; i++) {
        adj[i] = (int *)malloc(n * sizeof(int));
        for (int j = 0; j < n; j++) {
            adj[i][j] = 0;
        }
    }
    for (int i = 0; i < n - 1; i++) {
        adj[p[i]][i + 1] = 1;
    }

    printf("POSSIBLE\n");
    free(p);
    free(x);
    for(int i=0; i<n; ++i) free(adj[i]);
    free(adj);
    return 0;
}
