import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        long ans = 0;
        long prob = 1;
        for (int i = 1; ; i++) {
            ans += (long) i * (long) (Math.pow(2, m)) * (long) (n - m) * 100 + (long) i * (long) (Math.pow(2, m)) * m * 1900;
            prob *= 2;
            if (Math.pow(2, m) >= prob) break;

        }
        System.out.println(ans);
    }
}
