#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<pair<long long, long long>> balls(2 * n);
  for (int i = 0; i < 2 * n; ++i) {
    cin >> balls[i].first >> balls[i].second;
  }

  long long count = 0;
  vector<int> p(2 * n);
  for (int i = 0; i < 2 * n; ++i) {
    p[i] = i;
  }

  do {
    vector<bool> collected(2 * n, false);
    vector<pair<long long, long long>> robots_a(n);
    vector<pair<long long, long long>> robots_b(n);
    for (int i = 0; i < n; ++i) {
      robots_a[i] = {i + 1, 0};
      robots_b[i] = {0, i + 1};
    }

    bool possible = true;
    vector<bool> a_used(n, false);
    vector<bool> b_used(n, false);

    for (int i = 0; i < 2 * n; ++i) {
      int robot_index = p[i];
      if (robot_index < n) { 
        long long x = robots_a[robot_index].first;
        long long min_y = -1;
        int min_y_index = -1;
        for (int j = 0; j < 2 * n; ++j) {
          if (!collected[j] && balls[j].first == x) {
            if (min_y == -1 || balls[j].second < min_y) {
              min_y = balls[j].second;
              min_y_index = j;
            }
          }
        }
        if (min_y_index != -1) {
          collected[min_y_index] = true;
        }
      } else { 
        long long y = robots_b[robot_index - n].second;
        long long min_x = -1;
        int min_x_index = -1;
        for (int j = 0; j < 2 * n; ++j) {
          if (!collected[j] && balls[j].second == y) {
            if (min_x == -1 || balls[j].first < min_x) {
              min_x = balls[j].first;
              min_x_index = j;
            }
          }
        }
        if (min_x_index != -1) {
          collected[min_x_index] = true;
        }
      }
    }
    bool all_collected = true;
    for (int i = 0; i < 2 * n; ++i) {
      if (!collected[i]) {
        all_collected = false;
        break;
      }
    }
    if (all_collected) count++;
  } while (next_permutation(p.begin(), p.end()));

  cout << count % 1000000007 << endl;
  return 0;
}
