import java.util.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[][] balls = new int[2 * n][2];
        for (int i = 0; i < 2 * n; i++) {
            balls[i][0] = sc.nextInt();
            balls[i][1] = sc.nextInt();
        }
        long ans = 0;
        List<Integer> p = new ArrayList<>();
        for (int i = 0; i < 2 * n; i++) {
            p.add(i);
        }
        do {
            if (check(n, balls, p)) {
                ans++;
            }
        } while (nextPermutation(p));
        System.out.println(ans % MOD);
    }

    static boolean check(int n, int[][] balls, List<Integer> p) {
        boolean[] used = new boolean[2 * n];
        int[][] robots = new int[2 * n][2];
        for (int i = 1; i <= n; i++) {
            robots[i - 1][0] = i;
            robots[i - 1][1] = 0;
        }
        for (int i = n; i < 2 * n; i++) {
            robots[i][0] = 0;
            robots[i][1] = i - n + 1;
        }
        for (int i : p) {
            int type = i < n ? 0 : 1;
            int idx = i < n ? i : i - n;
            int x = robots[i][0];
            int y = robots[i][1];
            int min_idx = -1;
            if (type == 0) {
                int min_y = Integer.MAX_VALUE;
                for (int j = 0; j < 2 * n; j++) {
                    if (!used[j] && balls[j][0] == x && balls[j][1] < min_y) {
                        min_y = balls[j][1];
                        min_idx = j;
                    }
                }
            } else {
                int min_x = Integer.MAX_VALUE;
                for (int j = 0; j < 2 * n; j++) {
                    if (!used[j] && balls[j][1] == y && balls[j][0] < min_x) {
                        min_x = balls[j][0];
                        min_idx = j;
                    }
                }
            }
            if (min_idx != -1) used[min_idx] = true;
        }
        for (boolean b : used) {
            if (!b) return false;
        }
        return true;
    }

    static boolean nextPermutation(List<Integer> p) {
        int n = p.size();
        int i = n - 2;
        while (i >= 0 && p.get(i) >= p.get(i + 1)) i--;
        if (i == -1) return false;
        int j = n - 1;
        while (p.get(j) <= p.get(i)) j--;
        Collections.swap(p, i, j);
        Collections.reverse(p.subList(i + 1, n));
        return true;
    }
}
