def solve():
    n = int(input())
    balls = []
    for _ in range(2 * n):
        balls.append(list(map(int, input().split())))

    ans = 0

    import itertools

    robots = [(i + 1, 0) for i in range(n)] + [(0, i + 1) for i in range(n)]

    for order in itertools.permutations(robots):
        collected = [False] * (2 * n)
        ball_pos = {}
        for i in range(2 * n):
            ball_pos[(balls[i][0],balls[i][1])] = i

        possible = True

        for r in order:
            if r[1] == 0:
                min_y = float('inf')
                min_y_ball = -1
                for i in range(2 * n):
                    if balls[i][0] == r[0] and not collected[i]:
                        if balls[i][1] < min_y:
                            min_y = balls[i][1]
                            min_y_ball = i
                if min_y_ball != -1:
                    collected[min_y_ball] = True
            else:
                min_x = float('inf')
                min_x_ball = -1
                for i in range(2 * n):
                    if balls[i][1] == r[1] and not collected[i]:
                        if balls[i][0] < min_x:
                            min_x = balls[i][0]
                            min_x_ball = i
                if min_x_ball != -1:
                    collected[min_x_ball] = True

        all_collected = all(collected)
        if all_collected:
            ans += 1

    print(ans % 1000000007)

solve()
