#include <stdio.h>

int main() {
  int n;
  scanf("%d", &n);
  int seats[100001] = {0};
  int total_people = 0;
  for (int i = 0; i < n; i++) {
    int l, r;
    scanf("%d %d", &l, &r);
    for (int j = l; j <= r; j++) {
      if (seats[j] == 0) {
        seats[j] = 1;
        total_people++;
      }
    }
  }
  printf("%d\n", total_people);
  return 0;
}
