#include <stdio.h>
#include <stdbool.h>

int main() {
    int n;
    scanf("%d", &n);

    bool sheet[1000001] = {false};
    int count = 0;

    for (int i = 0; i < n; i++) {
        int a;
        scanf("%d", &a);
        if (sheet[a]) {
            sheet[a] = false;
        } else {
            sheet[a] = true;
        }
    }

    for (int i = 0; i <= 1000000; i++) {
        if (sheet[i]) {
            count++;
        }
    }

    printf("%d\n", count);
    return 0;
}
