#include <iostream>
#include <set>

using namespace std;

int main() {
  int n;
  cin >> n;

  set<long long> s;
  for (int i = 0; i < n; ++i) {
    long long a;
    cin >> a;
    if (s.count(a)) {
      s.erase(a);
    } else {
      s.insert(a);
    }
  }
  cout << s.size() << endl;
  return 0;
}
