import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        Set<Integer> s = new HashSet<>();
        for (int i = 0; i < n; i++) {
            int a = sc.nextInt();
            if (s.contains(a)) {
                s.remove(a);
            } else {
                s.add(a);
            }
        }
        System.out.println(s.size());
    }
}
