#include <stdio.h>
#include <limits.h>

int main() {
    int N, M, R;
    scanf("%d %d %d", &N, &M, &R);
    int r[R];
    for (int i = 0; i < R; i++) {
        scanf("%d", &r[i]);
    }
    int A[M], B[M], C[M];
    for (int i = 0; i < M; i++) {
        scanf("%d %d %d", &A[i], &B[i], &C[i]);
    }
    int min_dist = INT_MAX;
    int perm[R];
    for (int i = 0; i < R; i++) {
        perm[i] = i;
    }
    do {
        int dist = 0;
        for (int i = 0; i < R - 1; i++) {
            int start = r[perm[i]];
            int end = r[perm[i + 1]];
            int min_edge = INT_MAX;
            for (int j = 0; j < M; j++) {
                if ((A[j] == start && B[j] == end) || (A[j] == end && B[j] == start)) {
                    min_edge = C[j];
                    break;
                }
            }
            dist += min_edge;
        }
        min_dist = (dist < min_dist) ? dist : min_dist;
    } while (next_permutation(perm, perm + R));
    printf("%d\n", min_dist);
    return 0;
}
