import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int r = sc.nextInt();
        int[] towns = new int[r];
        for (int i = 0; i < r; i++) {
            towns[i] = sc.nextInt();
        }
        int[][] edges = new int[m][3];
        for (int i = 0; i < m; i++) {
            edges[i][0] = sc.nextInt();
            edges[i][1] = sc.nextInt();
            edges[i][2] = sc.nextInt();
        }
        int minDist = Integer.MAX_VALUE;
        List<Integer> permutation = new ArrayList<>();
        for (int i = 0; i < r; i++) {
            permutation.add(i);
        }
        do {
            int dist = 0;
            for (int i = 0; i < r - 1; i++) {
                int start = towns[permutation.get(i)];
                int end = towns[permutation.get(i + 1)];
                dist += dijkstra(start, end, n, edges);
            }
            minDist = Math.min(minDist, dist);
        } while (nextPermutation(permutation));
        System.out.println(minDist);
    }

    static int dijkstra(int start, int end, int n, int[][] edges) {
        int[] dist = new int[n + 1];
        Arrays.fill(dist, Integer.MAX_VALUE);
        dist[start] = 0;
        PriorityQueue<int[]> pq = new PriorityQueue<>(Comparator.comparingInt(a -> a[1]));
        pq.offer(new int[]{start, 0});
        while (!pq.isEmpty()) {
            int[] curr = pq.poll();
            int u = curr[0];
            int d = curr[1];
            if (d > dist[u]) continue;
            for (int[] edge : edges) {
                int v = -1;
                if (edge[0] == u) v = edge[1];
                if (edge[1] == u) v = edge[0];
                if (v != -1) {
                    if (dist[v] > dist[u] + edge[2]) {
                        dist[v] = dist[u] + edge[2];
                        pq.offer(new int[]{v, dist[v]});
                    }
                }
            }
        }
        return dist[end];
    }

    static boolean nextPermutation(List<Integer> permutation) {
        int i = permutation.size() - 2;
        while (i >= 0 && permutation.get(i) >= permutation.get(i + 1)) {
            i--;
        }
        if (i < 0) return false;
        int j = permutation.size() - 1;
        while (permutation.get(j) <= permutation.get(i)) {
            j--;
        }
        Collections.swap(permutation, i, j);
        Collections.reverse(permutation.subList(i + 1, permutation.size()));
        return true;
    }
}
