import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        long X = scanner.nextLong();
        long t = scanner.nextLong();
        long result = X - t;
        if (result < 0) {
            System.out.println(0);
        } else {
            System.out.println(result);
        }
        scanner.close();
    }
}
