#include <stdio.h>
#include <stdlib.h>

int cmp(const void *a, const void *b) {
    return *(int *)a - *(int *)b;
}

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    qsort(a, n, sizeof(int), cmp);
    int count = 1;
    int maxCount = 1;
    for (int i = 1; i < n; i++) {
        if (a[i] == a[i - 1] || a[i] == a[i - 1] + 1 || a[i] == a[i - 1] + 2) {
            count++;
        } else {
            maxCount = (maxCount > count) ? maxCount : count;
            count = 1;
        }
    }
    maxCount = (maxCount > count) ? maxCount : count;
    printf("%d\n", maxCount);
    return 0;
}
