#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int p[n];
    int count = 0;
    for (int i = 0; i < n; i++) {
        scanf("%d", &p[i]);
        if (p[i] == i + 1)
            count++;
    }
    if (count == 0)
        printf("0\n");
    else if (count == n)
        printf("%d\n", n -1);

    else if (count == n-1){
        int i;
        for(i=0;i<n;i++){
            if(p[i]!=i+1) break;
        }

        if(p[i]==i+2){
            printf("1\n");
        } else {
            printf("2\n");
        }
    }

    else
        printf("%d\n", count);
    return 0;
}
