import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] p = new int[n];
        for (int i = 0; i < n; i++) {
            p[i] = sc.nextInt();
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (p[i] == i + 1) {
                if (i + 1 < n && p[i + 1] != i + 2) {
                    int temp = p[i];
                    p[i] = p[i + 1];
                    p[i + 1] = temp;
                    ans++;
                } else if (i + 1 < n) {
                    int j = i + 2;
                    while (j < n && p[j] == j + 1) {
                        j++;
                    }
                    if (j < n) {
                        for (int k = j; k > i; k--) {
                            int temp = p[k];
                            p[k] = p[k - 1];
                            p[k - 1] = temp;
                            ans++;
                        }
                    } else {
                        ans++;
                        break;
                    }
                }
            }
        }
        System.out.println(ans);
    }
}
