def solve():
    n = int(input())
    p = list(map(int, input().split()))

    ans = 0

    for i in range(n):
        if p[i] == i + 1:
            j = i + 1
            while j < n and p[j] != i + 1:
                j += 1

            if j == n:
                continue

            for k in range(j, i, -1):
                p[k], p[k-1] = p[k-1], p[k]
                ans += 1

    print(ans)

solve()
