#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    int counts[200000] = {0};
    for (int i = 0; i < n; i++) {
        counts[a[i] + 100000]++;
        counts[a[i] + 100000 + 1]++;
        counts[a[i] + 100000 - 1]++;
    }
    int max_count = 0;
    for (int i = 0; i < 200000; i++) {
        if (counts[i] > max_count) {
            max_count = counts[i];
        }
    }
    printf("%d\n", max_count);
    return 0;
}
