#include <iostream>
#include <map>

using namespace std;

int main() {
  int n;
  cin >> n;

  map<long long, long long> counts;
  for (int i = 0; i < n; ++i) {
    long long a;
    cin >> a;
    counts[a]++;
    counts[a - 1]++;
    counts[a + 1]++;
  }

  long long max_count = 0;
  for (auto const& [key, val] : counts) {
    max_count = max(max_count, val);
  }

  cout << max_count << endl;
  return 0;
}
