#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    int p[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &p[i]);
        p[i]--; 
    }
    long long ans = 0;
    int vis[n];
    for (int i = 0; i < n; i++) {
        vis[i] = 0;
    }
    for (int i = 0; i < n; i++) {
        if (vis[i] == 0 && p[i] == i) {
            int j = i;
            int cycle_len = 0;
            while (vis[j] == 0) {
                vis[j] = 1;
                j = p[j];
                cycle_len++;
            }
            if (cycle_len > 0) {
                ans += cycle_len;
            }

        }
    }
    printf("%lld\n", ans);
    return 0;
}
