#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<int> p(n);
  for (int i = 0; i < n; ++i) {
    cin >> p[i];
  }

  long long count = 0;
  bool satisfied = true;
  for (int i = 0; i < n; ++i) {
    if (p[i] == i + 1) {
      satisfied = false;
      break;
    }
  }

  if (satisfied) {
    cout << 0 << endl;
    return 0;
  }

  for (int i = 0; i < n; ++i) {
    if (p[i] == i + 1) {
      for (int j = i + 1; j < n; ++j) {
        if (p[j] != j + 1) {
          for (int k = j; k > i; --k) {
            swap(p[k], p[k - 1]);
            count++;
          }
          break;
        }
      }
    }
  }
  cout << count << endl;
  return 0;
}
