import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] p = new int[n];
        for (int i = 0; i < n; i++) {
            p[i] = sc.nextInt();
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (p[i] == i + 1) {
                int j = i + 1;
                while (j < n && p[j] != i + 1) {
                    j++;
                }
                if (j == n) continue;
                for (int k = j; k > i; k--) {
                    int temp = p[k];
                    p[k] = p[k - 1];
                    p[k - 1] = temp;
                    ans++;
                }
            }
        }
        System.out.println(ans);
    }
}
