#include <stdio.h>
#include <stdlib.h>

#define MOD 998244353

typedef struct {
    long long x, y;
} Point;

long long cross_product(Point a, Point b, Point c) {
    return (b.x - a.x) * (c.y - a.y) - (b.y - a.y) * (c.x - a.x);
}

int main() {
    int n;
    scanf("%d", &n);

    Point points[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &points[i].x, &points[i].y);
    }

    long long total_score = 0;

    for (int i = 0; i < (1 << n); i++) {
        int count = 0;
        Point subset[n];
        for (int j = 0; j < n; j++) {
            if ((i >> j) & 1) {
                subset[count++] = points[j];
            }
        }

        if (count < 3) continue;

        int convex = 1;
        for (int j = 0; j < count; j++) {
            if (cross_product(subset[j], subset[(j + 1) % count], subset[(j + 2) % count]) <= 0) {
                convex = 0;
                break;
            }
        }

        if (convex) {
            int inside_count = 0;
            for (int j = 0; j < n; j++) {
                int inside = 1;
                for (int k = 0; k < count; k++) {
                    if (cross_product(subset[k], subset[(k + 1) % count], points[j]) < 0) {
                        inside = 0;
                        break;
                    }
                }
                if (inside) inside_count++;
            }
            long long score = 1;
            for (int j = 0; j < inside_count - count; j++) {
                score = (score * 2) % MOD;
            }
            total_score = (total_score + score) % MOD;
        }
    }

    printf("%lld\n", total_score);

    return 0;
}
