import java.util.*;
import java.math.*;

public class Main {
    static final int MOD = 998244353;

    static long cross(long x1, long y1, long x2, long y2) {
        return x1 * y2 - x2 * y1;
    }

    static boolean isConvex(List<Point> polygon) {
        if (polygon.size() < 3) return true;
        boolean isPositive = cross(polygon.get(1).x - polygon.get(0).x, polygon.get(1).y - polygon.get(0).y, polygon.get(2).x - polygon.get(1).x, polygon.get(2).y - polygon.get(1).y) > 0;
        for (int i = 1; i < polygon.size() -1; ++i) {
            long c = cross(polygon.get(i).x - polygon.get(i - 1).x, polygon.get(i).y - polygon.get(i - 1).y, polygon.get(i + 1).x - polygon.get(i).x, polygon.get(i + 1).y - polygon.get(i).y);
            if ((c > 0) != isPositive) return false;
        }
        long c = cross(polygon.get(polygon.size() - 1).x - polygon.get(polygon.size() - 2).x, polygon.get(polygon.size() - 1).y - polygon.get(polygon.size() - 2).y, polygon.get(0).x - polygon.get(polygon.size() - 1).x, polygon.get(0).y - polygon.get(polygon.size() - 1).y);
        return (c > 0) == isPositive;
    }

    static class Point {
        long x, y;
        Point(long x, long y) {
            this.x = x;
            this.y = y;
        }
    }

    static int countPoints(List<Point> polygon, List<Point> points) {
        int count = 0;
        for (Point p : points) {
            boolean inside = true;
            for (int i = 0; i < polygon.size(); ++i) {
                long c = cross(polygon.get(i).x - polygon.get((i-1+polygon.size())%polygon.size()).x, polygon.get(i).y - polygon.get((i-1+polygon.size())%polygon.size()).y, p.x - polygon.get((i-1+polygon.size())%polygon.size()).x, p.y - polygon.get((i-1+polygon.size())%polygon.size()).y);
                if (c < 0) {
                    inside = false;
                    break;
                }
            }
            if (inside) count++;
        }
        return count;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<Point> points = new ArrayList<>();
        for (int i = 0; i < n; ++i) {
            points.add(new Point(sc.nextLong(), sc.nextLong()));
        }
        long ans = 0;
        for (int i = 0; i < (1 << n); ++i) {
            List<Point> subset = new ArrayList<>();
            for (int j = 0; j < n; ++j) {
                if ((i >> j) % 2 == 1) {
                    subset.add(points.get(j));
                }
            }
            if (subset.size() < 3) continue;
            if (isConvex(subset)) {
                int inside = countPoints(subset, points);
                ans = (ans + (long)Math.pow(2, inside - subset.size())) % MOD;
            }
        }
        System.out.println(ans);
    }
}
