#include <stdio.h>

int main() {
    long long X, K, Q;
    scanf("%lld", &X);
    scanf("%lld", &K);
    long long r[K];
    for (int i = 0; i < K; i++) {
        scanf("%lld", &r[i]);
    }
    scanf("%lld", &Q);
    for (int i = 0; i < Q; i++) {
        long long t, a;
        scanf("%lld %lld", &t, &a);
        long long upper = a;
        long long lower = X - a;
        int turn = 0;
        for (int j = 0; j < K; j++) {
            if (t <= r[j]) break;
            if (turn % 2 == 0) {
                if (upper >= r[j]) {
                    upper -= r[j];
                    lower += r[j];
                } else {
                    lower += upper;
                    upper = 0;
                }
            } else {
                if (lower >= r[j] - r[j-1]) {
                    lower -= (r[j] - (j > 0 ? r[j-1] : 0));
                    upper += (r[j] - (j > 0 ? r[j-1] : 0));
                } else {
                    upper += lower;
                    lower = 0;
                }
            }
            turn++;
        }
        if (turn % 2 == 0) {
            if (upper >= t - (turn > 0 ? r[turn - 1] : 0)) {
                upper -= (t - (turn > 0 ? r[turn - 1] : 0));
            } else {
                upper = 0;
            }
        } else {
            if (lower >= t - (turn > 0 ? r[turn - 1] : 0)) {
                lower -= (t - (turn > 0 ? r[turn - 1] : 0));
            } else {
                lower = 0;
            }
            upper = X - lower;

        }
        printf("%lld\n", upper);
    }
    return 0;
}
