#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long x;
  cin >> x;

  int k;
  cin >> k;

  vector<long long> r(k);
  for (int i = 0; i < k; ++i) {
    cin >> r[i];
  }

  int q;
  cin >> q;

  for (int i = 0; i < q; ++i) {
    long long t, a;
    cin >> t >> a;

    long long current_a = a;
    bool upper_a = true;

    for (int j = 0; j < k; ++j) {
      if (t < r[j]) {
        if (upper_a) {
          current_a -= min(current_a, t);
        } else {
          current_a += min(x - current_a, t);
        }
        break;
      } else {
        if (upper_a) {
          current_a -= min(current_a, r[j] - (j == 0 ? 0 : r[j-1]));
        } else {
          current_a += min(x - current_a, r[j] - (j == 0 ? 0 : r[j-1]));
        }
        upper_a = !upper_a;
      }
    }
    if (upper_a) {
        current_a -= min(current_a, t - (k == 0 ? 0 : r[k-1]));
    } else {
        current_a += min(x - current_a, t - (k == 0 ? 0 : r[k-1]));
    }
    cout << current_a << endl;
  }
  return 0;
}
