import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long X = sc.nextLong();
        int K = sc.nextInt();
        long[] r = new long[K];
        for (int i = 0; i < K; i++) {
            r[i] = sc.nextLong();
        }
        int Q = sc.nextInt();
        for (int i = 0; i < Q; i++) {
            long t = sc.nextLong();
            long a = sc.nextLong();
            long ans = a;
            boolean upperA = true;
            for (int j = 0; j < K; j++) {
                if (t <= r[j]) break;
                if (upperA) {
                    long diff = r[j] - (j == 0 ? 0 : r[j - 1]);
                    if (ans >= diff) {
                        ans -= diff;
                    } else {
                        ans = 0;
                    }
                } else {
                    long diff = r[j] - (j == 0 ? 0 : r[j - 1]);
                    ans = X - ans;
                    if (ans >= diff) {
                        ans -= diff;
                    } else {
                        ans = 0;
                    }
                    ans = X - ans;

                }
                upperA = !upperA;
            }
            if (upperA) {
                if (ans >= t - (K == 0 ? 0 : r[K - 1])) {
                    ans -= t - (K == 0 ? 0 : r[K - 1]);
                } else {
                    ans = 0;
                }
            } else {
                ans = X - ans;
                if (ans >= t - (K == 0 ? 0 : r[K - 1])) {
                    ans -= t - (K == 0 ? 0 : r[K - 1]);
                } else {
                    ans = 0;
                }
                ans = X - ans;
            }
            System.out.println(ans);
        }
    }
}
