X = int(input())
K = int(input())
r = list(map(int, input().split()))
Q = int(input())

for _ in range(Q):
    t, a = map(int, input().split())
    upper = a
    lower = X - a
    turn_count = 0
    for i in range(K):
        if t <= r[i]:
            break
        if turn_count % 2 == 0:
            if upper >= r[i] - (r[i-1] if i > 0 else 0):
                upper -= r[i] - (r[i-1] if i > 0 else 0)
                lower += r[i] - (r[i-1] if i > 0 else 0)
            else:
                lower += upper
                upper = 0
        else:
            if lower >= r[i] - (r[i-1] if i > 0 else 0):
                lower -= r[i] - (r[i-1] if i > 0 else 0)
                upper += r[i] - (r[i-1] if i > 0 else 0)
            else:
                upper += lower
                lower = 0
        turn_count += 1
    if turn_count % 2 == 0:
        upper -= t - (r[turn_count-1] if turn_count > 0 else 0)
        if upper < 0:
            upper = 0
    else:
        lower -= t - (r[turn_count-1] if turn_count > 0 else 0)
        if lower < 0:
            lower = 0
        upper = X - lower

    print(upper)

