#include <stdlib.h>

int main() {
    long long Q, H, S, D;
    long long N;
    scanf("%lld %lld %lld %lld", &Q, &H, &S, &D);
    scanf("%lld", &N);
    long long ans = 1e18;
    for (long long i = 0; i <= N * 4; ++i) {
        for (long long j = 0; j <= N * 2; ++j) {
            long long rem = N * 1000 - i * 250 - j * 500;
            if (rem < 0) continue;
            if (rem % 1000 == 0) {
                long long k = rem / 1000;
                ans = ans < i * Q / 250 + j * H / 500 + k * S ? ans : i * Q / 250 + j * H / 500 + k * S;
            }
        }
    }
    for (long long i = 0; i <= N * 2; ++i) {
        long long rem = N * 1000 - i * 500;
        if (rem < 0) continue;
        if (rem % 1000 == 0) {
            long long k = rem / 1000;
            ans = ans < i * H / 500 + k * S ? ans : i * H / 500 + k * S;

        }
    }
    for (long long i = 0; i <=N; ++i) {
        long long rem = N - i;
        if (rem < 0) continue;
        ans = ans < i * S + rem * S ? ans : i * S + rem * S;
    }
    for (long long i = 0; i <= N / 2; ++i) {
        long long rem = N - i * 2;
        if (rem < 0) continue;
        ans = ans < i * D + rem * S ? ans : i * D + rem * S;

    }
    printf("%lld\n", ans);
    return 0;
}
