#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int main() {
    char a[200001];
    scanf("%s", a);
    int n = strlen(a);
    int count = 0;
    char *strings[100000000];
    strings[0] = strdup(a);
    count++;

    for (int i = 0; i < n; i++) {
        for (int j = i; j < n; j++) {
            char temp[200001];
            strcpy(temp, a);
            int k = 0;
            for (int l = j; l >= i; l--) {
                temp[k + i] = a[l];
                k++;
            }
            int flag = 0;
            for (int l = 0; l < count; l++) {
                if (strcmp(temp, strings[l]) == 0) {
                    flag = 1;
                    break;
                }
            }
            if (!flag) {
                strings[count] = strdup(temp);
                count++;
            }
        }
    }
    printf("%d\n", count);
    for(int i=0; i<count; ++i) free(strings[i]);
    return 0;
}
