import java.util.*;
import java.lang.Math;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long x1 = sc.nextLong();
        long y1 = sc.nextLong();
        long x2 = sc.nextLong();
        long y2 = sc.nextLong();
        int n = sc.nextInt();
        long[] X = new long[n];
        long[] Y = new long[n];
        for (int i = 0; i < n; i++) {
            X[i] = sc.nextLong();
            Y[i] = sc.nextLong();
        }
        double ans = Math.abs(x1 - x2) * 100 + Math.abs(y1 - y2) * 100;
        for (int i = 0; i < (1 << n); i++) {
            double cur = 0;
            long cur_x = x1;
            long cur_y = y1;
            List<Integer> used = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    used.add(j);
                }
            }
            Collections.sort(used, (a, b) -> {
                long dist_a = Math.abs(X[a] - cur_x) + Math.abs(Y[a] - cur_y);
                long dist_b = Math.abs(X[b] - cur_x) + Math.abs(Y[b] - cur_y);
                return Long.compare(dist_a, dist_b);
            });
            for (int j : used) {
                cur += Math.sqrt(Math.pow(X[j] - cur_x, 2) + Math.pow(Y[j] - cur_y, 2)) * 100;
                cur_x = X[j];
                cur_y = Y[j];
            }
            cur += Math.abs(x2 - cur_x) * 100 + Math.abs(y2 - cur_y) * 100;
            ans = Math.min(ans, cur);
        }
        System.out.println(ans);
    }
}
