#include <stdio.h>
#include <string.h>
#include <limits.h>

int main() {
    char a[2001], b[2001];
    scanf("%s", a);
    scanf("%s", b);
    int n = strlen(a);
    int ans = INT_MAX;
    for (int i = 0; i < n; i++) {
        char temp_a[2001];
        strcpy(temp_a, a);
        int count = i;
        for (int j = 0; j < n; j++) {
            if (temp_a[j] - '0' != b[j] - '0') {
                int k = j;
                while (k < n && b[k] == '0')
                    k++;
                if (k == n) {
                    count = INT_MAX;
                    break;
                }
                count++;
                temp_a[k] = (temp_a[k] == '0' ? '1' : '0');
            }
        }
        ans = (ans < count) ? ans : count;
    }
    if (ans == INT_MAX)
        printf("-1\n");
    else
        printf("%d\n", ans);
    return 0;
}

