#include <iostream>
#include <string>
#include <algorithm>
#include <queue>
#include <map>

using namespace std;

int main() {
    string a, b;
    cin >> a >> b;

    queue<pair<string, int>> q;
    q.push({a, 0});

    map<string, int> visited;
    visited[a] = 0;

    while (!q.empty()) {
        string current_a = q.front().first;
        int current_dist = q.front().second;
        q.pop();

        if (current_a == b) {
            cout << current_dist << endl;
            return 0;
        }

        string next_a;


        for (int i = 0; i < b.length(); ++i) {
            if (b[i] == '1') {
                next_a = current_a;
                next_a[i] = (next_a[i] == '0' ? '1' : '0');
                if (visited.find(next_a) == visited.end()) {
                    visited[next_a] = current_dist + 1;
                    q.push({next_a, current_dist + 1});
                }
            }
        }


        next_a = current_a.substr(1) + current_a[0];
        if (visited.find(next_a) == visited.end()) {
            visited[next_a] = current_dist + 1;
            q.push({next_a, current_dist + 1});
        }


        next_a = current_a.substr(current_a.length() - 1) + current_a.substr(0, current_a.length() - 1);
        if (visited.find(next_a) == visited.end()) {
            visited[next_a] = current_dist + 1;
            q.push({next_a, current_dist + 1});
        }
    }

    cout << -1 << endl;
    return 0;
}
