import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String a = sc.next();
        String b = sc.next();
        int n = a.length();
        int ans = Integer.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            String cur_a = a;
            int cur_ans = 0;
            int diff = 0;
            for (int j = 0; j < n; j++) {
                if (cur_a.charAt(j) != b.charAt(j)) {
                    diff++;
                }
            }
            if (diff == 0) {
                ans = Math.min(ans, cur_ans + i);
                continue;
            }
            boolean possible = true;
            for (int j = 0; j < n; j++) {
                if (b.charAt(j) == '1') {
                    if (cur_a.charAt(j) == '0') {
                        cur_ans++;
                        cur_a = cur_a.substring(0, j) + '1' + cur_a.substring(j + 1);
                    }
                }
            }
            int shifts = 0;
            while (true) {
                boolean all_same = true;
                for (int k = 0; k < n; k++) {
                    if (cur_a.charAt(k) != b.charAt(k)) {
                        all_same = false;
                        break;
                    }
                }
                if (all_same) {
                    ans = Math.min(ans, cur_ans + i + shifts);
                    break;
                }
                if (shifts >= n) {
                    possible = false;
                    break;
                }
                cur_a = cur_a.substring(1) + cur_a.charAt(0);
                shifts++;
            }
        }
        if (ans == Integer.MAX_VALUE) {
            System.out.println(-1);
        } else {
            System.out.println(ans);
        }
    }
}
