#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 998244353

long long factorial(int n) {
    long long res = 1;
    for (int i = 2; i <= n; i++)
        res = (res * i) % MOD;
    return res;
}

int main() {
    char a[10005], b[10005];
    scanf("%s", a);
    scanf("%s", b);
    int n = strlen(a);
    int count_a = 0, count_b = 0;
    int pos_a[10005], pos_b[10005];
    for (int i = 0; i < n; i++) {
        if (a[i] == '1') {
            count_a++;
            pos_a[count_a - 1] = i;
        }
        if (b[i] == '1') {
            count_b++;
            pos_b[count_b - 1] = i;
        }
    }
    long long fact_k = factorial(count_a);
    long long ans = 0;
    int p[10005];
    for (int i = 0; i < count_a; i++) p[i] = i;
    do {
        int q[10005];
        for (int i = 0; i < count_a; i++) q[i] = i;
        do {
            char temp_a[10005];
            strcpy(temp_a, a);
            for (int i = 0; i < count_a; i++) {
                char temp = temp_a[pos_a[p[i]]];
                temp_a[pos_a[p[i]]] = temp_a[pos_b[q[i]]];
                temp_a[pos_b[q[i]]] = temp;
            }
            if (strcmp(temp_a, b) == 0) ans++;
        } while (next_permutation(q, q + count_a));
    } while (next_permutation(p, p + count_a));
    printf("%lld\n", (ans * fact_k % MOD * fact_k % MOD) % MOD);
    return 0;
}
