import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String a = sc.next();
        String b = sc.next();
        int n = a.length();
        List<Integer> a1 = new ArrayList<>();
        List<Integer> b1 = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            if (a.charAt(i) == '1') a1.add(i);
            if (b.charAt(i) == '1') b1.add(i);
        }
        int k = a1.size();
        long ans = 0;
        if (k == 0) {
            System.out.println(1);
            return;
        }
        int[] p = new int[k];
        int[] q = new int[k];
        for (int i = 0; i < k; i++) {
            p[i] = i;
            q[i] = i;
        }
        long mod = 998244353;
        long fact = 1;
        for (int i = 1; i <= k; i++) fact = fact * i % mod;
        fact = fact * fact % mod;
        do {
            do {
                String cur = a;
                for (int i = 0; i < k; i++) {
                    char temp = cur.charAt(a1.get(p[i]));
                    cur = cur.substring(0, a1.get(p[i])) + cur.charAt(b1.get(q[i])) + cur.substring(a1.get(p[i]) + 1);
                    cur = cur.substring(0, b1.get(q[i])) + temp + cur.substring(b1.get(q[i]) + 1);
                }
                if (cur.equals(b)) ans++;
            } while (nextPermutation(q));
        } while (nextPermutation(p));
        System.out.println(ans);
    }

    static boolean nextPermutation(int[] p) {
        int n = p.length;
        int i = n - 2;
        while (i >= 0 && p[i] >= p[i + 1]) i--;
        if (i < 0) return false;
        int j = n - 1;
        while (p[j] <= p[i]) j--;
        swap(p, i, j);
        reverse(p, i + 1, n - 1);
        return true;
    }

    static void swap(int[] p, int i, int j) {
        int temp = p[i];
        p[i] = p[j];
        p[j] = temp;
    }

    static void reverse(int[] p, int i, int j) {
        while (i < j) {
            swap(p, i, j);
            i++;
            j--;
        }
    }
}
