#include <stdio.h>

long long power(long long base, long long exp, long long mod) {
    long long res = 1;
    base %= mod;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        exp /= 2;
    }
    return res;
}

long long modInverse(long long n, long long mod) {
    return power(n, mod - 2, mod);
}

int main() {
    long long n, m;
    scanf("%lld %lld", &n, &m);
    long long mod = 998244353;
    if (n == m) {
        printf("%lld\n", (n + 1) * modInverse(2, mod) % mod);
    } else if (n > m) {
        long long ans = 0;
        long long num = n;
        long long den = n + m;
        ans = (num * modInverse(den, mod)) % mod;
        printf("%lld\n",ans);
    }
    else {
        long long ans = 0;
        long long num = m;
        long long den = n + m;
        ans = (num * modInverse(den, mod)) % mod;
        printf("%lld\n",ans);
    }
    return 0;
}
