import java.util.*;
import java.math.*;

public class Main {
    static final long MOD = 998244353;

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if (b % 2 == 1) res = res * a % MOD;
            a = a * a % MOD;
            b /= 2;
        }
        return res;
    }

    static long inv(long a) {
        return pow(a, MOD - 2);
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long m = sc.nextLong();

        if (n == m) {
            System.out.println(n * inv(2) % MOD);
            return;
        }

        long p = 0, q = 1;
        if (n > m) {
            long temp = n;
            n = m;
            m = temp;
        }

        long[][] dp = new long[n + 1][m + 1];
        dp[0][0] = 1;
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= m; j++) {
                if (i + 1 <= n) {
                    dp[i + 1][j] = (dp[i + 1][j] + dp[i][j] * (n - i)) % MOD;
                }
                if (j + 1 <= m) {
                    dp[i][j + 1] = (dp[i][j + 1] + dp[i][j] * (m - j)) % MOD;
                }
            }
        }

        for (int i = 0; i <= n; i++) {
            p = (p + dp[i][m - i] * (i + 1)) % MOD;
            q = (q + dp[i][m - i]) % MOD;

        }
        System.out.println(p * inv(q) % MOD);

    }
}
