MOD = 998244353

def pow_mod(x, y, p):
    res = 1
    x = x % p
    while y > 0:
        if y & 1:
            res = (res * x) % p
        y = y >> 1
        x = (x * x) % p
    return res

def inv_mod(x, p):
    return pow_mod(x, p - 2, p)

N, M = map(int, input().split())

if N == M:
    ans = (N + M) * inv_mod(2, MOD) % MOD
else:
    dp = {}
    def solve(n, m):
        if (n, m) in dp:
            return dp[(n, m)]
        if n == 0:
            return 0
        if m == 0:
            return n

        res = (solve(n - 1, m) * m + solve(n, m - 1) * n) * inv_mod(n + m, MOD) % MOD
        res = (res + 1) % MOD
        dp[(n, m)] = res
        return res

    ans = solve(N, M)

print(ans)

