#include <stdio.h>
#include <string.h>
#include <stdbool.h>

int main() {
    char s[100001];
    scanf("%s", s);
    bool chars[26] = {false};
    for (int i = 0; i < strlen(s); i++) {
        chars[s[i] - 'a'] = true;
    }
    for (int i = 0; i < 26; i++) {
        if (!chars[i]) {
            printf("%c\n", 'a' + i);
            return 0;
        }
    }
    printf("None\n");
    return 0;
}
