#include <iostream>
#include <string>
#include <set>

using namespace std;

int main() {
  string s;
  cin >> s;

  set<char> chars;
  for (char c : s) {
    chars.insert(c);
  }

  for (char c = 'a'; c <= 'z'; ++c) {
    if (chars.find(c) == chars.end()) {
      cout << c << endl;
      return 0;
    }
  }

  cout << "None" << endl;
  return 0;
}
