import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        boolean[] seen = new boolean[26];
        for (char c : s.toCharArray()) {
            seen[c - 'a'] = true;
        }
        for (int i = 0; i < 26; i++) {
            if (!seen[i]) {
                System.out.println((char) ('a' + i));
                return;
            }
        }
        System.out.println("None");
    }
}
