#include <stdio.h>
#include <limits.h>
#include <queue>

using namespace std;

int sum_digits(long long n) {
    int sum = 0;
    while (n > 0) {
        sum += n % 10;
        n /= 10;
    }
    return sum;
}

int main() {
    long long k;
    scanf("%lld", &k);

    queue<pair<long long, int>> q;
    q.push({0, 0});

    int min_sum = INT_MAX;
    bool visited[100000] = {false};

    while (!q.empty()) {
        long long num = q.front().first;
        int sum = q.front().second;
        q.pop();

        if (num % k == 0 && num >0) {
            min_sum = min(min_sum, sum_digits(num));
            continue;
        }

        for (int i = 0; i <= 9; ++i) {
            long long next_num = num * 10 + i;
            if (next_num <= 1000000000LL && next_num >0)
            {
                int next_sum = sum + i;
                if (next_sum < min_sum && !visited[next_num % k]) {
                    q.push({next_num, next_sum});
                    visited[next_num % k] = true;
                }
            }

        }
    }
    printf("%d\n", min_sum);
    return 0;
}
